# frozen_string_literal: true

require 'spec_helper'

class ValidatingHttpClient
  VALID_KEYS = %w[host authorization x-amz-date x-amz-security-token x-amz-content-sha256].freeze
  def get_response(uri, headers)
    header_keys = headers.keys.map(&:downcase)
    unless (header_keys - VALID_KEYS).empty?
      raise ArgumentError, "Invalid headers: #{header_keys - VALID_KEYS}"
    end
    Net::HTTP.get_response(uri, headers)
  end
end

RSpec.describe(Authentication::AuthnIam::Authenticator) do
  let(:authenticator) { Authentication::AuthnIam::Authenticator.new(env:[], client: ValidatingHttpClient.new) }
  let(:conjur_role) { 'host/foo/188945769008/conjur-role' }

  # Good headers
  let(:valid_global_headers) { '{"host":"sts.amazonaws.com","x-amz-date":"20230518T152525Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGDI4QX56/20230518/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=81b929060b45f05470c9f542a9e46cdce51d37c1d22a58d1942b7fa175079af5"}' }
  let(:valid_regional_headers) { '{"host":"sts.eu-central-1.amazonaws.com","x-amz-date":"20230518T152442Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGDI4QX56/20230518/eu-central-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=8e0bebec9a3ce860b4595a4b27710da558a157a711e2dcbfe3a86881af99c459"}' }
  let(:valid_global_headers_no_host) { '{"Authorization":"AWS4-HMAC-SHA256 Credential=ASIAYNRQATHTPJYFX7PM/20230613/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-date;x-amz-security-token, Signature=e81afd905d5131d697e33ad38a5eff72789ed3e6b5e61d694212fbfe09684a73","X-Amz-Date":"20230613T204702Z","X-Amz-Security-Token":""}' }
  let(:valid_regional_headers_no_host) { '{"Authorization":"AWS4-HMAC-SHA256 Credential=ASIAYNRQATHTEQFKJN2P/20230613/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-date;x-amz-security-token, Signature=38f8b60e4cbee78d55f379646e4fde87439ce0e43cd4cae38e3d2e295ebcfc58","X-Amz-Date":"20230613T204734Z","X-Amz-Security-Token":""}' }
  let(:invalid_regional_headers_no_host) { '{"Authorization":"AWS4-HMAC-SHA256 Credential=ASIAYNRQATHTEQFKJN2P/20230613/evil.org?us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-date;x-amz-security-token, Signature=38f8b60e4cbee78d55f379646e4fde87439ce0e43cd4cae38e3d2e295ebcfc58","X-Amz-Date":"20230613T204734Z","X-Amz-Security-Token":""}' }
  let(:malicious_regional_headers_no_host) { "{\"Authorization\":\"AWS4-HMAC-SHA256 Credential=ASIAYNRQATHTEQFKJN2P/20230613/evil.org;..amazonaws.com?q=1&z=\\t\\nus-east-1/sts/aws4_request, SignedHeaders=host;x-amz-date;x-amz-security-token, Signature=38f8b60e4cbee78d55f379646e4fde87439ce0e43cd4cae38e3d2e295ebcfc58\",\"X-Amz-Date\":\"20230613T204734Z\",\"X-Amz-Security-Token\":\"\"}" }
  let(:with_x_forwarded_host) { '{"X-Forwarded-Host": "evil.org","host":"sts.amazonaws.com","x-amz-date":"20230518T152525Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGDI4QX56/20230518/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=81b929060b45f05470c9f542a9e46cdce51d37c1d22a58d1942b7fa175079af5"}' }


  # Bad headers
  let(:expired_headers) { '{"host":"sts.eu-central-1.amazonaws.com","x-amz-date":"20230518T152442Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGDI4QX56/20230518/eu-central-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=8e0bebec9a3ce860b4595a4b27710da558a157a711e2dcbfe3a86881af99c459"}' }
  let(:different_signing_request_uri) { '{"host":"sts.amazonaws.com","x-amz-date":"20230518T153130Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGDI4QX56/20230518/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=2f0162e0124dfeb105bc8a318efb93272797e2762a4fe93010437594a0d0244d"}' }
  let(:different_signing_request_verb) { '{"host":"sts.amazonaws.com","x-amz-date":"20230518T153042Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGDI4QX56/20230518/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=bd7f1e549999b6854adfb7c35e344efa38d74bac1c9078fbc34f71f4ff90173b"}' }
  let(:global_non_global_signer) { '{"host":"sts.amazonaws.com","x-amz-date":"20230518T155824Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGFOUEXPF/20230518/eu-north-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=e643e2bb5d737954951f34ff67c4a11e00d022b402579f85ff9d99a5f70cbc53"}' }
  let(:regional_headers_signed_for_another_region) { '{"host":"sts.us-west-1.amazonaws.com","x-amz-date":"20230518T160804Z","x-amz-security-token":"","x-amz-content-sha256":"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855","authorization":"AWS4-HMAC-SHA256 Credential=ASIASX7QLUIYGFOUEXPF/20230518/eu-north-1/sts/aws4_request, SignedHeaders=host;x-amz-content-sha256;x-amz-date;x-amz-security-token, Signature=bfaefe50abefa68b0081af52687885a7391006f2c8b0d80b6c20beb7da808d56"}' }
  let(:invalid_authorization_header) { '{"Authorization":"AWS4-HMAC-SHA256 Credential=bad_request, SignedHeaders=host;x-amz-date;x-amz-security-token, Signature=38f8b60e4cbee78d55f379646e4fde87439ce0e43cd4cae38e3d2e295ebcfc58","X-Amz-Date":"20230613T204734Z","X-Amz-Security-Token":""}' }
  let(:invalid_host) { '{"host":"evil.org","Authorization":"AWS4-HMAC-SHA256 Credential=ASIAYNRQATHTEQFKJN2P/20230613/us-east-1/sts/aws4_request, SignedHeaders=host;x-amz-date;x-amz-security-token, Signature=38f8b60e4cbee78d55f379646e4fde87439ce0e43cd4cae38e3d2e295ebcfc58","X-Amz-Date":"20230613T204734Z","X-Amz-Security-Token":""}' }

  describe '.valid?' do
    context 'headers are valid' do
      context 'when using the global STS endpoint' do
        context 'with request signed by `us-east-1`' do
          let(:payload) do
            double('AuthenticationParameters', credentials: valid_global_headers, username: conjur_role)
          end
          it 'succeeds', vcr: 'authenticators/authn-iam/valid-global-headers' do
            expect(authenticator.valid?(payload)).to be(true)
          end
        end
        context 'with request signed by `us-east-1` and no `host` header' do
          let(:payload) do
            double('AuthenticationParameters', credentials: valid_global_headers_no_host, username: conjur_role)
          end
          it 'succeeds', vcr: 'authenticators/authn-iam/valid-global-headers-no-host' do
            expect(authenticator.valid?(payload)).to be(true)
          end
        end
        context 'with request signed for a non `us-east-1` region' do
          let(:payload) do
            double('AuthenticationParameters', credentials: global_non_global_signer, username: conjur_role)
          end
          it 'fails', vcr: 'authenticators/authn-iam/global-sts-non-global-signer' do
            expect { authenticator.valid?(payload) }
              .to raise_error(
                Errors::Authentication::AuthnIam::InvalidAWSHeaders,
                'CONJ00018E Invalid or expired AWS headers: Credential should be scoped to a valid region.'
              )
          end
        end
      end
      context 'when using the regional STS endpoint' do
        context 'when regional endpoint request was signed for that region' do
          let(:payload) do
            double('AuthenticationParameters', credentials: valid_regional_headers, username: conjur_role)
          end
          it 'succeeds', vcr: 'authenticators/authn-iam/valid-regional-headers' do
            expect(authenticator.valid?(payload)).to be(true)
          end
        end
        context 'when regional endpoint request was signed for that region and no `host` header' do
          let(:payload) do
            double('AuthenticationParameters', credentials: valid_regional_headers_no_host, username: conjur_role)
          end
          it 'succeeds', vcr: 'authenticators/authn-iam/valid-regional-headers-no-host' do
            expect(authenticator.valid?(payload)).to be(true)
          end
        end
        context 'when regional endpoint request was signed for that region and no `host` header with unsupported region' do
          let(:payload) do
            double('AuthenticationParameters', credentials: invalid_regional_headers_no_host, username: conjur_role)
          end
          it 'fails', vcr: 'authenticators/authn-iam/invalid-regional-headers-no-host' do
            expect { authenticator.valid?(payload) }
              .to raise_error(
                    Errors::Authentication::AuthnIam::InvalidAWSHeaders,
                    'CONJ00018E Invalid or expired AWS headers: Failed to extract AWS region from authorization header'
                  )
          end
        end
        context 'when regional endpoint request was signed for that region and no `host` header with malicious region' do
          let(:payload) do
            double('AuthenticationParameters', credentials: malicious_regional_headers_no_host, username: conjur_role)
          end
          it 'fails', vcr: 'authenticators/authn-iam/invalid-regional-headers-no-host' do
            expect { authenticator.valid?(payload) }
              .to raise_error(
                    Errors::Authentication::AuthnIam::InvalidAWSHeaders,
                    'CONJ00018E Invalid or expired AWS headers: Failed to extract AWS region from authorization header'
                  )
          end
        end
        context 'when regional endpoint request was signed for another region' do
          let(:payload) do
            double('AuthenticationParameters', credentials: regional_headers_signed_for_another_region, username: conjur_role)
          end
          it 'fails', vcr: 'authenticators/authn-iam/regional-headers-signed-for-another-region' do
            expect { authenticator.valid?(payload) }
              .to raise_error(
                Errors::Authentication::AuthnIam::InvalidAWSHeaders,
                'CONJ00018E Invalid or expired AWS headers: Credential should be scoped to a valid region.'
              )
          end
        end
      end
    end
    context 'when headers are invalid' do
      context 'when headers have expired' do
        let(:payload) do
          double('AuthenticationParameters', credentials: expired_headers, username: conjur_role)
        end
        it 'fails with exception', vcr: 'authenticators/authn-iam/expired-headers' do
          expect { authenticator.valid?(payload) }
            .to raise_error(
              Errors::Authentication::AuthnIam::InvalidAWSHeaders,
              'CONJ00018E Invalid or expired AWS headers: Signature expired: 20230518T152442Z is now earlier than 20230518T153438Z (20230518T154938Z - 15 min.)'
            )
        end
      end
      context 'for the signing request' do
        context 'when the verb is not GET' do
          let(:payload) do
            double('AuthenticationParameters', credentials: different_signing_request_verb, username: conjur_role)
          end
          it 'fails with exception', vcr: 'authenticators/authn-iam/invalid-signing-request-verb' do
            expect { authenticator.valid?(payload) }
              .to raise_error(
                Errors::Authentication::AuthnIam::InvalidAWSHeaders,
                'CONJ00018E Invalid or expired AWS headers: The request signature we calculated does not match the signature you provided. Check your AWS Secret Access Key and signing method. Consult the service documentation for details.'
              )
          end
        end
        context 'when the signed URI is different' do
          let(:payload) do
            double('AuthenticationParameters', credentials: different_signing_request_uri, username: conjur_role)
          end
          it 'fails', vcr: 'authenticators/authn-iam/different-signing-request-url' do
            expect { authenticator.valid?(payload) }
              .to raise_error(
                Errors::Authentication::AuthnIam::InvalidAWSHeaders,
                'CONJ00018E Invalid or expired AWS headers: The request signature we calculated does not match the signature you provided. Check your AWS Secret Access Key and signing method. Consult the service documentation for details.'
              )
          end
        end
      end
      context 'when the authorization header is invalid' do
        let(:payload) do
          double('AuthenticationParameters', credentials: invalid_authorization_header, username: conjur_role)
        end
        it 'fails' do
          expect { authenticator.valid?(payload) }
            .to raise_error(
              Errors::Authentication::AuthnIam::InvalidAWSHeaders,
              'CONJ00018E Invalid or expired AWS headers: Failed to extract AWS region from authorization header'
            )
        end
      end
    end
    context 'when an http exception occurs' do
      let(:authenticator) do
        Authentication::AuthnIam::Authenticator.new(
          env: [],
          client: class_double(Net::HTTP).tap do |double|
            allow(double).to receive(:get_response).and_raise(Timeout::Error)
          end
        )
      end
      let(:payload) do
        double('AuthenticationParameters', credentials: valid_global_headers, username: conjur_role)
      end
      it 'fails' do
        expect { authenticator.valid?(payload) }
          .to raise_error(
            Errors::Authentication::AuthnIam::VerificationError,
            'CONJ00063E Verification of IAM identity failed with exception: Timeout::Error'
          )
      end
    end
  end
  context 'for different roles' do
    context 'when login is a User' do
      let(:payload) do
        # file deepcode ignore HardcodedCredential: This is a test code, not an actual credential
        double('AuthenticationParameters', username: 'foo/bar/baz/188945769008/conjur-role', credentials: valid_global_headers)
      end
      it 'succeeds', vcr: 'authenticators/authn-iam/valid-global-headers' do
        expect(authenticator.valid?(payload)).to be(true)
      end
      context 'when login is in the root policy' do
        let(:payload) do
          double('AuthenticationParameters', username: '188945769008/conjur-role', credentials: valid_global_headers)
        end
        it 'fails', vcr: 'authenticators/authn-iam/valid-global-headers' do
          expect(authenticator.valid?(payload)).to be(false)
        end
      end
    end
    context 'when login is a host' do
      context 'when role has multiple policy nestings' do
        let(:payload) do
          double('AuthenticationParameters', username: 'host/foo/bar/baz/188945769008/conjur-role', credentials: valid_global_headers)
        end
        it 'succeeds', vcr: 'authenticators/authn-iam/valid-global-headers' do
          expect(authenticator.valid?(payload)).to be(true)
        end
      end
      context 'when account-id is missing from username' do
        let(:payload) do
          double('AuthenticationParameters', username: 'host/foo/bar/baz/conjur-role', credentials: valid_global_headers)
        end
        it 'fails', vcr: 'authenticators/authn-iam/valid-global-headers' do
          expect(authenticator.valid?(payload)).to be(false)
        end
      end
      context 'when login does not include an AWS resource id' do
        let(:payload) do
          double('AuthenticationParameters', username: 'host/foo/188945769008', credentials: valid_global_headers)
        end
        it 'fails', vcr: 'authenticators/authn-iam/valid-global-headers' do
          expect(authenticator.valid?(payload)).to be(false)
        end
      end
      context 'when login is in the root policy' do
        let(:payload) do
          double('AuthenticationParameters', username: 'host/188945769008/conjur-role', credentials: valid_global_headers)
        end
        it 'succeeds', vcr: 'authenticators/authn-iam/valid-global-headers' do
          expect(authenticator.valid?(payload)).to be(true)
        end
      end
      context 'when invalid host is passed' do
        let(:payload) do
          double('AuthenticationParameters', username: 'host/foo/bar/baz/188945769008/conjur-role', credentials: invalid_host)
        end
        it 'fails' do
          expect { authenticator.valid?(payload) }.to raise_error(
             Errors::Authentication::AuthnIam::InvalidAWSHeaders,
             'CONJ00018E Invalid or expired AWS headers: Headers validation failed'
          )
        end
      end
      context 'invalid headers are ignored' do
        let(:payload) do
          double('AuthenticationParameters', username: 'host/188945769008/conjur-role', credentials: with_x_forwarded_host)
        end
        it 'succeeds', vcr: 'authenticators/authn-iam/valid-global-headers' do
          expect(authenticator.valid?(payload)).to be(true)
        end
      end
    end
  end
end
